import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import useEventService from '../services/eventService';
import { useAuth } from '../context/AuthContext';
import './Events.css';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loaded, setLoaded] = useState(false); // Nueva variable de estado para controlar si los eventos ya se cargaron
  const [error, setError] = useState(null);
  const { getEvents } = useEventService();
  const { token } = useAuth();

  const fetchEvents = useCallback(async () => {
    try {
      const data = await getEvents();
      setEvents(data);
      setLoading(false);
      setLoaded(true); // Marcar que los eventos se han cargado correctamente
    } catch (err) {
      setError(err.message || 'Failed to fetch events');
      setLoading(false);
    }
  }, [getEvents]);

  useEffect(() => {
    if (token && !loaded) { // Solo carga los eventos si el token está disponible y aún no se han cargado
      fetchEvents();
    }
  }, [token, loaded, fetchEvents]);

  if (loading) {
    return <div className="events-container"><p>Cargando...</p></div>;
  }

  return (
    <div className="events-container">
      <h2>Eventos</h2>
      {error && <p className="error-message">{error}</p>}
      {events.length === 0 ? (
        <p>No hay eventos disponibles.</p>
      ) : (
        <ul className="events-list">
          {events.map(event => (
            <li key={event.id}>
              <Link to={`/partidos/${event.id}`}>{event.name}</Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Events;
