import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import useEventService from '../services/eventService';
import { useAuth } from '../context/AuthContext';
import Header from './Header';
import { Modal, Button } from 'react-bootstrap';
import './Predictions.css';
import countCodService from '../services/countCodService';
import eventPredictionsService from '../services/eventPredictionsService'; // Importa el servicio para consultar las predicciones del evento
import Swal from 'sweetalert2';

const Predictions = () => {
  const navigate = useNavigate(); // Usamos useNavigate para manejar la redirección
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [availableCodes, setAvailableCodes] = useState(0);
  const [codesFetched, setCodesFetched] = useState(false);
  const [eventsFetched, setEventsFetched] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  // eslint-disable-next-line no-unused-vars
  const [showNoCodesModal, setShowNoCodesModal] = useState(false);
  const [showParticipatedModal, setShowParticipatedModal] = useState(false); // Estado para mostrar el modal si ya ha participado en el evento
  // eslint-disable-next-line no-unused-vars
  const [showRedeemConfirmationModal, setShowRedeemConfirmationModal] = useState(false); // Estado para mostrar el modal de confirmación
  const { getEvents } = useEventService();
  const { token } = useAuth();
  const [showAlert, setShowAlert] = useState(false);
  var id = null;

  useEffect(() => {
    if (showAlert) {
      Swal.fire({
        title: 'No tienes códigos disponibles',
        text: 'Por favor, adquiere códigos para poder redimir.',
        icon: 'info',
        confirmButtonText: 'Entendido',
        customClass: {
          confirmButton: 'btn btn-primary',
        },
        buttonsStyling: false,
      }).then(() => {
        setShowAlert(false);
      });
    }
  }, [showAlert]);

  const fetchEvents = useCallback(async () => {
    try {
      const data = await getEvents();
      setEvents(data);
      setLoading(false);
      setEventsFetched(true);
    } catch (err) {
      setError(err.message || 'No se pudo obtener los eventos');
      setLoading(false);
    }
  }, [getEvents]);

  const fetchAvailableCodes = useCallback(async () => {
    try {
      const response = await countCodService(token);
      setAvailableCodes(response.count);
      setCodesFetched(true);
    } catch (error) {
      console.error('Error fetching available codes:', error);
    }
  }, [token]);

  const handleRedeemConfirmation = () => {
    setShowRedeemConfirmationModal(false);
    if (selectedEvent) {
      window.location.href = `/predicciones/${selectedEvent.id}`;
    }
  };

  const checkParticipation = async (eventId) => {
    try {
      const response = await eventPredictionsService(eventId, token); // Incluye el token de sesión en la solicitud
      if (response.length > 0) {
        Swal.fire({
          title: 'Ya has participado en este evento',
          text: 'Ya has realizado predicciones para este evento. No puedes participar más de una vez.',
          icon: 'warning',
          confirmButtonText: 'Ver resultados',
          customClass: {
            confirmButton: 'btn btn-warning',
          },
          buttonsStyling: false,
      }).then((results) => {
        if (results.isConfirmed) {
          navigate(`/resultados/${id}`);
        }
      });
        // setShowParticipatedModal(true);
      } else {
        window.location.href = `/predicciones/${eventId}`;
      }
    } catch (error) {
      console.error('Error checking participation:', error);
    }
  };



  useEffect(() => {
    if (token && !codesFetched) {
      fetchAvailableCodes();
    }
  }, [token, codesFetched, fetchAvailableCodes]);

  useEffect(() => {
    if (token && !eventsFetched) {
      fetchEvents();
    }
  }, [token, eventsFetched, fetchEvents]);

  if (loading) {
    return <div className="events-container"><p>Cargando...</p></div>;
  }

  return (
    <div className="events-container">
      <Header />
      <div className="external-bg-container-include d-flex justify-content-center align-items-start pt-5 vh-100">
        <div className="container d-flex justify-content-center align-items-center flex-column">
          <div className="col-12 m-0 p-0">
            <h1 className="text-center title-text-ranking">Eventos</h1>
            <div className="container-event">
              <div className="container">
                <div className="row g-lg-4 g-2">
                  {error && <p className="error-message">{error}</p>}
                  {events.map(event => (
                    <div key={event.id} className="col-xl-3 col-lg-4 col-md-4 col-sm-6 col-6">
                      <div
                        className={`event-card text ${availableCodes === 0 ? 'disabled-event' : ''}`}
                        onClick={() => {
                          if (availableCodes === 0) {
                            setShowAlert(true);
                          } else {
                            setSelectedEvent(event);
                            checkParticipation(event.id); // Verificar si ya ha participado en el evento
                            id = event.id
                          }
                        }}
                      >
                        <div className="single-event">
                          <div className="content-event-title">
                            {event.name}
                          </div>
                        </div>

                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* <Modal show={showNoCodesModal} onHide={() => setShowNoCodesModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>No tienes códigos disponibles</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Por favor, adquiere códigos para poder redimir.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowNoCodesModal(false)}>Cerrar</Button>
        </Modal.Footer>
      </Modal> */}


      <Modal show={showParticipatedModal} onHide={() => setShowParticipatedModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Ya has participado en este evento</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Ya has realizado predicciones para este evento. No puedes participar más de una vez.
        </Modal.Body>
        <Modal.Footer>
          {/* Utilizamos navigate para redirigir al usuario a la página de resultados */}
          <Button variant="secondary" onClick={() => {
            setShowParticipatedModal(false);
            navigate(`/resultados/${selectedEvent.id}`); // Redirección a la página de resultados
          }}>Ver resultados</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Predictions;
