import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import registerService from '../services/registerService';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    email: '',
    dni: '',
    name: '',
    city: '',
    phone: ''
  });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const dniRegex = /^\d{10}$/;
    const phoneRegex = /^\d{10}$/;

    const newErrors = {};

    // Validating email
    if (!formData.email || !emailRegex.test(formData.email)) {
      newErrors.email = 'Por favor, ingrese una dirección de correo electrónico válida.';
    }

    // Validating dni
    if (!formData.dni || !dniRegex.test(formData.dni)) {
      newErrors.dni = 'Por favor, ingrese una cédula válida (10 dígitos).';
    }

    // Validating phone
    if (!formData.phone || !phoneRegex.test(formData.phone)) {
      newErrors.phone = 'Por favor, ingrese un número de teléfono válido (10 dígitos).';
    }

    if (Object.keys(newErrors).length === 0) {
      try {
        await registerService.register(formData);
        setSuccessMessage('¡Registro exitoso! Ahora puede iniciar sesión.');
        setFormData({
          email: '',
          dni: '',
          name: '',
          city: '',
          phone: ''
        });
      } catch (err) {
        setErrors(err);
      }
    } else {
      setErrors(newErrors);
    }
  };

  const handleLoginRedirect = () => {
    navigate('/login');
  };

  return (
    <div className="external-container d-flex justify-content-center align-items-center vh-100">

    </div>
  );
};

export default Register;
