import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import API_BASE_URL from '../config.js';

const useEventPredictionService = () => {
  const { token } = useAuth();

  const savePredictions = async (eventId, predictions) => {
    if (!token) {
      throw new Error('No token available');
    }

    const response = await axios.post(`${API_BASE_URL}/save-predictions/${eventId}/`, 
      { predictions },
      {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      }
    );

    return response.data;
  };

  return {
    savePredictions,
  };
};

export default useEventPredictionService;
