import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import useGameService from '../services/gameService'; // Importa el servicio de juegos
import './Games.css'; // Agrega el archivo CSS

const Games = () => {
  const { eventId } = useParams(); // Obtiene el ID del evento de los parámetros de la URL
  const { getGames } = useGameService(); // Obtén la función getGames del servicio

  const [games, setGames] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const data = await getGames(eventId); // Llama a la función getGames con el ID del evento
        setGames(data);
      } catch (err) {
        setError(err.message || 'Failed to fetch games');
      }
    };

    // Verifica si el eventId ha cambiado antes de llamar fetchGames
    if (eventId) {
      fetchGames();
    }
  }, [eventId, getGames]); // Asegúrate de incluir getGames como dependencia para que se actualice cuando cambie


  return (
    <div className="games-container">
      <h2>Partidos del evento {eventId}</h2>
      {error && <p className="error-message">{error}</p>}
      {games.length === 0 ? (
        <p>No hay partidos disponibles para este evento.</p>
      ) : (
        <ul className="games-list">
          {games.map(game => (
            <li key={game.id}>{/* Renderiza los detalles de cada partido */}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Games;
