import React from 'react';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import './Home.css'; // Asegúrate de importar el archivo CSS

const Home = () => {
  const { token, logout } = useAuth();

  return (
    <div className="home-container">
      <nav className="navbar">
        <Link to="/" className="nav-link">Home</Link>
        {!token && <Link to="/registro" className="nav-link">Registro</Link>}
        {!token && <Link to="/login" className="nav-link">Login</Link>}
        {token && <button className="nav-link logout-button" onClick={logout}>Cerrar sesión</button>}
      </nav>
      <h2>Home</h2>
      {token ? (
        <div>
          <p>Token de logueo: {token}</p>
        </div>
      ) : (
        <div>
          <p>No estás logueado.</p>
          <div className="auth-buttons">
            <Link to="/registro" className="auth-button">Registro</Link>
            <Link to="/login" className="auth-button">Login</Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
