// Partidos.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import useGameService from '../services/gameService';
import './Games.css';

const Games = () => {
  const [games, setGames] = useState([]);
  const [error, setError] = useState(null);
  const { id } = useParams();
  const { getGames } = useGameService();

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const data = await getGames(id); // Envía el ID del evento para obtener los partidos asociados
        setGames(data);
      } catch (err) {
        setError(err.message || 'Failed to fetch games');
      }
    };

    fetchGames();
  }, [id, getGames]);

  return (
    <div className="games-container">
      <h2>Partidos del Evento</h2>
      {error && <p className="error-message">{error}</p>}
      {games.length === 0 ? (
        <p>No hay partidos disponibles para este evento.</p>
      ) : (
        <ul className="games-list">
          {games.map(game => (
            <li key={game.id}>
              {/* Muestra la información del partido */}
              <p>{game.team1} vs {game.team2}</p>
              <p>{game.date}</p>
              {/* Agrega más detalles del partido según sea necesario */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Games;
