import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import { CodProvider } from './context/CodContext'; // Importa el proveedor del contexto CodContext
import Events from './components/Events';
import Games from './components/Games';
import Login from './components/Login';
import Register from './components/Register';
import Home from './components/Home';
import RedimirCodigos from './components/RedimirCodigos'; // Importa el componente para redimir códigos

const App = () => {
  return (
    <AuthProvider>
      <CodProvider> {/* Agrega el proveedor del contexto CodContext */}
        <Router>
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/registro" element={<Register />} />
            <Route path="/eventos" element={<Events />} />
            <Route path="/partidos/:eventId" element={<Games />} />
            <Route path="/redimir-codigos" element={<RedimirCodigos />} /> {/* Agrega la ruta para redimir códigos */}
            <Route path="/" element={<Home />} />
          </Routes>
        </Router>
      </CodProvider>
    </AuthProvider>
  );
};

export default App;
