import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import registerService from '../services/registerService';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    email: '',
    dni: '',
    name: '',
    city: '',
    phone: ''
  });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await registerService.register(formData);
      setSuccessMessage('Registration successful! You can now log in.');
      setFormData({
        email: '',
        dni: '',
        name: '',
        city: '',
        phone: ''
      });
    } catch (err) {
      setErrors(err);
    }
  };

  const handleLoginRedirect = () => {
    navigate('/login');
  };

  return (
    <div className="register-container">
      <h2>Register</h2>
      {successMessage ? (
        <div>
          <p>{successMessage}</p>
          <button onClick={handleLoginRedirect}>Go to Login</button>
        </div>
      ) : (
        <form className="register-form" onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          {errors.email && <p className="error-message">{errors.email}</p>}

          <input
            type="text"
            name="dni"
            placeholder="DNI"
            value={formData.dni}
            onChange={handleChange}
            required
          />
          {errors.dni && <p className="error-message">{errors.dni}</p>}

          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleChange}
            required
          />
          {errors.name && <p className="error-message">{errors.name}</p>}

          <input
            type="text"
            name="city"
            placeholder="City"
            value={formData.city}
            onChange={handleChange}
            required
          />
          {errors.city && <p className="error-message">{errors.city}</p>}

          <input
            type="text"
            name="phone"
            placeholder="Phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
          {errors.phone && <p className="error-message">{errors.phone}</p>}

          <button type="submit">Register</button>
        </form>
      )}
      {errors.non_field_errors && <p className="error-message">{errors.non_field_errors}</p>}
      <Link to="/" className="back-home-link">Volver al inicio</Link>
    </div>
  );
};

export default Register;
