import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import checkCodService from '../services/checkCodService';
import { useAuth } from '../context/AuthContext';
import { useCod } from '../context/CodContext'; // Importa el contexto de código
import Header from './Header';
import './RedimirCodigos.css';

const RedimirCodigos = () => {
  const { token } = useAuth();
  const { setCod } = useCod(); // Usar setCod del contexto de código
  const [cod, setCodInput] = useState('');
  const [isAvailable, setIsAvailable] = useState(false);
  const [msgResponse, setMsgResponse] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleCodChange = (event) => {
    setCodInput(event.target.value);
  };

  const handleCheckCod = async () => {
    try {
      const response = await checkCodService(cod, token);
      setIsAvailable(response.is_available);
      setMsgResponse(response.msg_response);
      setError('');
      if (response.is_available === "true") {
        setCod(cod); // Guarda el código en el contexto de código
        navigate('/eventos');
      }
    } catch (error) {
      setIsAvailable(false);
      setMsgResponse('El código no existe.');
      setError(error.message);
    }
  };

  return (
    <div className="redimir-codigos-container">
      <Header />
      <h2>Redimir Códigos</h2>
      <div className="form-container">
        <input
          type="text"
          value={cod}
          onChange={handleCodChange}
          placeholder="Ingrese el código"
        />
        <button onClick={handleCheckCod}>Verificar Código</button>
      </div>
      {error && <p className="error-message">{error}</p>}
      {msgResponse && (
        <p className={isAvailable === "true" ? "success-message" : "error-message"}>{msgResponse}</p>
      )}
    </div>
  );
};

export default RedimirCodigos;
