import axios from 'axios';

const BASE_URL = 'https://api.locosporlapromo.com/api';

const checkCodService = async (cod, token) => {
  try {
    const response = await axios.get(`${BASE_URL}/check-cod/?cod=${cod}`, {
      headers: {
        Authorization: `Bearer ${token}` // Agrega el token de sesión a los encabezados de la solicitud
      }
    });
    return response.data;
  } catch (error) {
    console.log(error);
    throw new Error(error.response.data.msg_response); // Imprime el error proporcionado por la API
  }
};

export default checkCodService;
