import React, { useEffect, useState } from 'react';
import Header from './Header';
import getUserRanking from '../services/getUserRanking';
import { useAuth } from '../context/AuthContext'; // Importa el contexto de autenticación
import './Ranking.css'; // Importa los estilos CSS para la tabla de puntajes

const Ranking = () => {
  const { token } = useAuth(); // Obtiene el token del contexto de autenticación
  const [userRanking, setUserRanking] = useState([]);

  useEffect(() => {
    fetchUserRanking();
  }, []);

  const fetchUserRanking = async () => {
    try {
      const data = await getUserRanking(token); // Pasa el token al servicio getUserRanking
      setUserRanking(data);
    } catch (error) {
      console.error('Error fetching user ranking:', error);
    }
  };

  return (
    <div className="events-container">
      <Header />
      <div className="external-bg-container-include d-flex justify-content-center align-items-start pt-5 vh-100">
      <div className="container d-flex justify-content-center align-items-center flex-column">
        <div className="col-12 m-0 p-0">
          <h1 className="text-center title-text-ranking">Ranking</h1>
          <div className="container table-container">
            <table className="table">
              <thead>
                <tr>
                  <th scope="col" className="firstcolumn">Posición</th>
                  <th scope="col" className="secondcolumn">Tendero</th>
                  <th scope="col" className="thirdcolumn">Puntaje</th>
                </tr>
              </thead>
              <tbody>
                {userRanking.map((user, index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{user.user}</td>
                    <td>{user.total_points}</td>
                  </tr>
                ))}
                {/* Aquí puedes agregar filas adicionales si es necesario */}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
    </div>
  );
};

export default Ranking;
