// Games.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './Games.css'; // Agrega el archivo CSS

const Games = () => {
  const { eventId } = useParams(); // Obtiene el ID del evento de los parámetros de la URL
  const [games, setGames] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const response = await fetch(`https://api.locosporlapromo.com/api/games/${eventId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch games');
        }
        const data = await response.json();
        setGames(data);
      } catch (err) {
        setError(err.message || 'Failed to fetch games');
      }
    };

    fetchGames();
  }, [eventId]);

  return (
    <div className="games-container">
      <h2>Partidos del evento {eventId}</h2>
      {error && <p className="error-message">{error}</p>}
      {games.length === 0 ? (
        <p>No hay partidos disponibles para este evento.</p>
      ) : (
        <ul className="games-list">
          {games.map(game => (
            <li key={game.id}>{/* Renderiza los detalles de cada partido */}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Games;
