import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Header.css'; // Importa el archivo CSS para estilos

const Header = () => {
  const { username, logout } = useAuth(); // Obtiene el nombre de usuario y la función de logout
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login'); // Redirige a la página de inicio de sesión después de cerrar sesión
  };

  return (
    <header className="header">
      <nav className="nav">
        <span className="username">Bienvenido, {username}</span>
        <button onClick={handleLogout} className="logout-button">Cerrar sesión</button>
      </nav>
    </header>
  );
};

export default Header;
