import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import authService from '../services/authService';
import './Login.css';  // Asegúrate de importar el archivo CSS

const Login = () => {
  const [email, setEmail] = useState('');
  const { login } = useAuth();
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await authService.login(email);
      const token = response.access;
      if (token) {
        login(token);
        navigate('/');
      } else {
        setError('Login failed. Token not found.');
      }
    } catch (err) {
      // Aquí se convierte el error en una cadena de texto legible
      setError(err.response.data.non_field_errors[0] || 'Login failed');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form className="login-form" onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default Login;
