import axios from 'axios';
import API_BASE_URL from '../config.js';

const registerEvent = async (userData) => {
  const token = localStorage.getItem('token');
  const savedUserId = localStorage.getItem('userId'); // Obtén el nombre de usuario del almacenamiento local

  console.log(userData,"userData", savedUserId)
  const saveRegisterEvent = async (userData) => {
    console.log("########");

    if (!token) {
      throw new Error('No token available');
    }

    const response = await axios.post(`${API_BASE_URL}/register-event/`, 
      { userData },
      {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      }
    );

    return response.data;
  };

  return {
    saveRegisterEvent,
  };
};

export default registerEvent
