import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import useGameService from '../services/gameService';
import './Games.css';

const Games = () => {
  const { eventId } = useParams();
  const { getGames } = useGameService();

  const [games, setGames] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showSubmitButton, setShowSubmitButton] = useState(false); // Estado para mostrar el bot贸n de enviar resultados

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const data = await getGames(eventId);
        setGames(data);
        setError(null);
        // Verifica si hay partidos disponibles para mostrar el bot贸n de enviar resultados
        setShowSubmitButton(data.length > 0);
      } catch (err) {
        setError(err.message || 'Failed to fetch games');
      } finally {
        setLoading(false);
      }
    };

    if (loading) {
      fetchGames();
    }
  }, [eventId, getGames, loading]);

  return (
    <div className="games-container">
      <Link to="/eventos" className="back-button">Volver a Eventos</Link>
      <h2>Partidos del evento {eventId}</h2>
      {loading && <p>Cargando...</p>}
      {games.length === 0 ? (
        <p>No hay partidos disponibles para este evento.</p>
      ) : (
        <table className="games-table">
          <thead>
            <tr>
              <th>Fecha</th>
              <th>Hora</th>
              <th>Equipo Local</th>
              <th>Equipo Visitante</th>
              <th>Resultado Local</th>
              <th>Resultado Visitante</th>
            </tr>
          </thead>
          <tbody>
            {games.map(game => (
              <tr key={game.id}>
                <td>{game.date_game}</td>
                <td>{game.time_game}</td>
                <td>{game.local_team}</td>
                <td>{game.visit_team}</td>
                <td><input type="text" placeholder="Resultado local" /></td>
                <td><input type="text" placeholder="Resultado visitante" /></td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      {showSubmitButton && <button className="submit-button">Enviar Resultados</button>}
    </div>
  );
};

export default Games;
