import React, { useState, useEffect } from 'react';
import eventService from '../services/eventService';
import './Events.css';  // Asegúrate de importar el archivo CSS

const Events = () => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const data = await eventService.getEvents();
        setEvents(data);
      } catch (err) {
        setError('Failed to fetch events');
      }
    };

    fetchEvents();
  }, []);

  const handleEventClick = (event) => {
    // Aquí manejarás el clic en un evento, podrías navegar a una nueva vista o mostrar información adicional
    console.log(event);
  };

  return (
    <div className="events-container">
      <h2>Eventos</h2>
      {error && <p className="error-message">{error}</p>}
      {events.length === 0 ? (
        <p>No hay eventos disponibles.</p>
      ) : (
        <ul className="events-list">
          {events.map(event => (
            <li key={event.id} onClick={() => handleEventClick(event)}>
              {event.name}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Events;
