import React, { useState, useEffect } from 'react';
import useEventPredictionsService from '../services/useEventPredictionsService';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Header from './Header';
import './EventPredictionsInfo.css';

const EventPredictionsInfo = () => {
    const { id_event } = useParams();
    const [predictions, setPredictions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { token } = useAuth();
    const { getEventPredictions } = useEventPredictionsService();

    useEffect(() => {
        const fetchPredictions = async () => {
            try {
                const data = await getEventPredictions(token, id_event);
                setPredictions(data);
                setLoading(false);
            } catch (error) {
                setError(error.message);
                setLoading(false);
            }
        };

        fetchPredictions(); // Llamada inicial

        // Retorno de la función de cleanup para cancelar la llamada si el componente se desmonta o si id_evento cambia antes de que se complete la llamada
        return () => {
            // Cancelar la llamada o limpiar recursos si es necesario
        };
    }, [id_event, token]); // Solo ejecutar el efecto si cambian id_evento, token o getEventPredictions

    if (loading) {
        return <p className="loading">Cargando...</p>;
    }

    if (error) {
        return <p className="error">Error: {error}</p>;
    }

    const baseUrl = 'https://api.locosporlapromo.com';

    return (
        <div className="events-container">
            <Header />
            <div className="external-bg-container-include d-flex justify-content-center align-items-start pt-5 vh-100">
                <div className='d-flex justify-content-center align-items-center flex-column'>
                    <div className="col-12 m-0 p-0">
                        <h3 className="text-center title-text-matches">Partidos del Evento</h3>
                        <div className="playing-bet">
                            <div className="container">
                                <div className="accordion-collapse collapse show sports-expanded-list">
                                    <div className="accordion-body sports-body">
                                        <div className="tournament-title">
                                            <span className="title-text">Partidos del Evento</span>
                                        </div>
                                        <div className="playing-sports-all table-matches-container">
                                            {predictions.map(prediction => (
                                                <div key={prediction.id} className="single-tournament">
                                                    <div className="tournament-title"></div>
                                                    <div className="all-tournament-match">
                                                        <div className="single-t-match">
                                                            <div className="match-time">
                                                                <span className="time-icon">
                                                                    <i className="fa-regular fa-clock"></i>
                                                                </span>
                                                                <span className="m-date">{prediction.date_game}</span>
                                                                <span className="m-time">{prediction.time_game}</span>
                                                            </div>
                                                            {/* <div className="playing-teams">
                                                                <div className="single-team mx-4">
                                                                    <div className="team-descr">
                                                                        <span className="team-icon">
                                                                        <img src={`${baseUrl}${prediction.local_team.flag_team}`} alt={prediction.local_team.name_team} />
                                                                        </span>
                                                                        <span className="team-name">{prediction.local_team.name_team}</span>
                                                                    </div>
                                                                </div>
                                                                <div className="single-team mx-4">
                                                                    <div className="team-descr">
                                                                        <span className="team-icon">
                                                                            <img src={`${baseUrl}${prediction.visit_team.flag_team}`} alt={prediction.visit_team.name_team} />
                                                                        </span>
                                                                        <span className="team-name">{prediction.visit_team.name_team}</span>
                                                                    </div>
                                                                </div>
                                                            </div> */}
                                                            {prediction.predictions && prediction.predictions.map(pred => (
                                                                <div key={pred.id} className="placing-bet">
                                                                    <div className="single-bet-place">
                                                                        <span className="team-icon">
                                                                            <img src={`${baseUrl}${prediction.local_team.flag_team}`} alt={prediction.local_team.name_team} />
                                                                        </span>
                                                                        <span className="team-name">{prediction.local_team.name_team}</span>
                                                                        <span className="team-goals fw-bold">{pred.local_team_goals}</span>
                                                                    </div>
                                                                    <div className="single-bet-place draw-box d-flex justify-content-center align-items-center">
                                                                        <span className="bet-ratio">VS</span>
                                                                    </div>
                                                                    <div className="single-bet-place">
                                                                        <span className="team-icon">
                                                                            <img src={`${baseUrl}${prediction.visit_team.flag_team}`} alt={prediction.visit_team.name_team} />
                                                                        </span>
                                                                        <span className="team-name">{prediction.visit_team.name_team}</span>
                                                                        <span className="team-goals fw-bold">{pred.visit_team_goals}</span>
                                                                    </div>
                                                                </div>
                                                            ))}
                                                        </div>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <Link to="/predicciones" className="btn btn-primary mt-3">Volver a Grupos</Link>
                </div>
            </div>
        </div>
    );
};

export default EventPredictionsInfo;
