import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import { CodProvider } from './context/CodContext';
import Predictions from './components/Predictions';
import EventGames from './components/EventGames';  // Importar el nuevo componente
import EventPredictionsInfo from './components/EventPredictionsInfo'; // Importar el nuevo componente
import Ranking from './components/Ranking';
import Login from './components/Login';
import Register from './components/Register';
import Home from './components/Home';
import RedimirCodigos from './components/RedimirCodigos';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

const App = () => {
  return (
    <AuthProvider>
      <CodProvider>
        <Router>
          <Routes>
            <Route path="/home" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/registro" element={<Register />} />
            <Route path="/predicciones" element={<Predictions />} />
            <Route path="/predicciones/:id_event" element={<EventGames />} />  {/* Nueva ruta para EventGames */}
            <Route path="/resultados/:id_event" element={<EventPredictionsInfo />} /> {/* Nueva ruta para EventPredictionsInfo */}
            <Route path="/activar-codigos" element={<RedimirCodigos />} /> 
            <Route path="/ranking" element={<Ranking />} /> 
            <Route path="*" element={<Navigate to="/home" />} />
          </Routes>
        </Router>
      </CodProvider>
    </AuthProvider>
  );
};

export default App;
