import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import checkCodService from '../services/checkCodService'; // Importa el servicio para verificar el código
import './RedimirCodigos.css'; // Importa el archivo CSS

const RedimirCodigos = () => {
  const [cod, setCod] = useState('');
  const [isAvailable, setIsAvailable] = useState(false);
  const [msgResponse, setMsgResponse] = useState('');
  const [error, setError] = useState('');

  const handleCodChange = (event) => {
    setCod(event.target.value);
  };

  const handleCheckCod = async () => {
    try {
      const response = await checkCodService(cod);
      setIsAvailable(response.is_available);
      setMsgResponse(response.msg_response);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="redimir-codigos-container">
      <h2>Redimir Códigos</h2>
      <div className="form-container">
        <input
          type="text"
          value={cod}
          onChange={handleCodChange}
          placeholder="Ingrese el código"
        />
        <button onClick={handleCheckCod}>Verificar Código</button>
      </div>
      {error && <p className="error-message">{error}</p>}
      {isAvailable ? (
        <p className="success-message">{msgResponse}</p>
      ) : (
        <p className="error-message">{msgResponse}</p>
      )}
      {isAvailable && <Link to="/eventos">Ir a la lista de eventos</Link>}
    </div>
  );
};

export default RedimirCodigos;
