import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import useEventService from '../services/eventService';
import './Events.css';  // Asegúrate de importar el archivo CSS

const Events = () => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState(null);
  const { getEvents } = useEventService();

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const data = await getEvents();
        setEvents(data);
      } catch (err) {
        console.error('Fetch events error:', err); // Depuración: capturar errores de fetchEvents
        setError(err.message || 'Failed to fetch events');
      }
    };

    fetchEvents();
  }, [getEvents]);

  const handleEventClick = (event) => {
    // Aquí manejarás el clic en un evento, podrías navegar a una nueva vista o mostrar información adicional
    console.log(event);
  };

  return (
    <div className="events-container">
      <h2>Eventos</h2>
      {error && <p className="error-message">{error}</p>}
      {events.length === 0 ? (
        <p>No hay eventos disponibles.</p>
      ) : (
        <ul className="events-list">
          {events.map(event => (
            <li key={event.id} onClick={() => handleEventClick(event)}>
              {event.name}
            </li>
          ))}
        </ul>
      )}
      <Link to="/" className="back-button">Volver al Home</Link>
    </div>
  );
};

export default Events;
