import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import checkCodService from '../services/checkCodService';
import { useAuth } from '../context/AuthContext';
import { useCod } from '../context/CodContext'; // Importa el contexto de código
import Header from './Header';
import Swal from 'sweetalert2';
import './RedimirCodigos.css';

const RedimirCodigos = () => {
  const { token } = useAuth();
  const { setCod } = useCod(); // Usar setCod del contexto de código
  const [cod, setCodInput] = useState('');
  const [response, setResponse] = useState(null);
  const [error, setError] = useState('');
  const [confirmation, setConfirmation] = useState(false); // Estado para controlar la visibilidad del modal
  const [canRedirect, setCanRedirect] = useState(false);
  const navigate = useNavigate();

  const handleCodChange = (event) => {
    setCodInput(event.target.value);
  };

  const handleCheckCod = async () => {
    // Validar que el código no esté en blanco y tenga exactamente 6 dígitos
    if (cod.trim() === '') {
      setError('El código no puede estar en blanco.');
      return;
    }

    if (!/^[A-Z0-9]{6}$/.test(cod)) {
      setError('El código debe tener exactamente 6 caracteres alfanuméricos.');
      return;
    }

    try {
      const response = await checkCodService(cod, token);
      setResponse(response);
      console.log('Response from checkCodService:', response); // Imprimir la respuesta en la consola

      // Si el mensaje de respuesta es de confirmación, mostrar el modal y limpiar el formulario
      if (response.msg_response && response.msg_response.includes('se encuentra disponible')) {
        console.log(response.msg_response)
        Swal.fire({
          title: '¡Código activado!',
          text: response.msg_response,
          icon: 'success',
          confirmButtonText: 'Aceptar',
          customClass: {
            confirmButton: 'btn btn-success',
          },
          buttonsStyling: false,
        }).then((result) => {
          if (result.isConfirmed) {
            navigate('/predicciones');
          }
        }) 

        setError('');
        setCodInput('');
      } else {
        Swal.fire({
          title: 'Código Asociado',
          text: response.msg_response,
          icon: 'error',
          confirmButtonText: 'Aceptar',
          customClass: {
            confirmButton: 'btn btn-danger',
          },
          buttonsStyling: false,
        }).then(() => {
          handleCloseModal();
        })

        setError('');
        setCodInput('');
      }

      // Verificar el contenido de la respuesta
      if (response.msg_response.includes('se encuentra disponible')) {
        setCanRedirect(true); // Permitir redirección
      } else if (response.msg_response.includes('ya fue canjeado')) {
        setCanRedirect(false); // No permitir redirección
      }
    } catch (error) {
      setError(error.message);
    }
  };

  const handleCloseModal = () => {
    setConfirmation(false); // Ocultar el modal
    if (canRedirect) {
      navigate('/predicciones'); // Redirigir a la página principal si está permitido
    }
  };

  return (
    <div className="events-container">
      <Header />
      <div className="external-bg-container-include d-flex justify-content-center align-items-start pt-5 vh-100">
        <div className="container d-flex justify-content-center align-items-center flex-column">
          <div className="col-12 m-0 p-0">
          </div>
          <div className="col-12 d-flex justify-content-center align-items-center">
            <img src="./logo.png" className="container-logo" alt="logo" />
          </div>
          <div className="col-12 m-0 p-0 mt-5 mb-3">
            <div className="d-flex justify-content-center align-items-center flex-column">
              <div className="col-12 col-lg-10 d-flex justify-content-center align-items-center flex-column">
                <div className="register-container">
                  <div className="register-form d-flex justify-content-center align-items-center" onSubmit={handleCheckCod}>
                    <div className="col-12 d-flex justify-content-center align-items-center flex-lg-row flex-column">
                      <div className="col-12 col-lg-6">
                        <div className="form-group w-100">
                          <input
                            type="text"
                            className="input-register-form"
                            name="name"
                            placeholder="Ingrese el código aquí"
                            value={cod}
                            onChange={handleCodChange}
                            required
                          />
                        </div>
                      </div>
                      <div className="col-12 col-lg-2 mx-3">
                        <div className="d-flex justify-content-center align-items-center">
                          <button onClick={handleCheckCod} className="btn-register-two Bebas-Neue">Validar Código</button>
                        </div>
                      </div>
                    </div>
                  </div>
                  {error && <p className="error-message W-75 d-flex justify-content-center align-items-center text-white">{error}</p>}
                </div>
              </div>
            </div>
          </div>
          {/* {confirmation && (
          // Modal de Bootstrap
            <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
                <div className="modal-dialog">
                    <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">¡Código activado!</h5>
                        <button type="button" className="btn-close" onClick={handleCloseModal}></button>
                    </div>
                    <div className="modal-body">
                        <p>{response.msg_response}</p>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-primary" onClick={handleCloseModal}>Aceptar</button>
                    </div>
                    </div>
                </div>
            </div>
          )} */}
        </div>
      </div>
    </div>
  );
};

export default RedimirCodigos;
