import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import API_BASE_URL from '../config';

const useEventPredictionsService = () => {
    const getEventPredictions = async (token, eventId) => {        
        try {
            const response = await axios.get(`${API_BASE_URL}/event/predictions/${eventId}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            return response.data;
        } catch (error) {
            console.error('Error fetching event predictions:', error);
        }
    };

    return { getEventPredictions };
};

export default useEventPredictionsService;
