import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventMatches = ({ eventId }) => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const response = await axios.get(`https://api.locosporlapromo.com/api/games/${eventId}/`);
        setMatches(response.data);
      } catch (error) {
        setError(error.message || 'No se pudieron cargar los partidos');
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [eventId]);

  if (loading) {
    return <div>Cargando...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>Partidos del Evento</h2>
      {matches.length === 0 ? (
        <p>No hay partidos disponibles para este evento.</p>
      ) : (
        <ul>
          {matches.map(match => (
            <li key={match.id}>
              {/* Mostrar la información del partido */}
              <p>Equipo Local: {match.local_team}</p>
              <p>Equipo Visitante: {match.visit_team}</p>
              {/* Agrega más detalles del partido según tu necesidad */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default EventMatches;
