import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import API_BASE_URL from '../config.js';

const useUserCodService = () => {
  const { token } = useAuth();

  const getUserCods = async () => {
    if (!token) {
      throw new Error('No token available');
    }

    const response = await axios.get(`${API_BASE_URL}/user-cods/`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    return response.data;
  };

  return {
    getUserCods,
  };
};

export default useUserCodService;
