import React, { useState, useEffect } from 'react';
import { useParams, Link, Navigate } from 'react-router-dom';
import useEventService from '../services/eventService';
import useEventPredictionService from '../services/eventPredictionService';
import useUserCodService from '../services/userCodService';
import { useAuth } from '../context/AuthContext';
import Header from './Header';
import Swal from 'sweetalert2';
import { Modal, Button } from 'react-bootstrap';
import './EventGames.css';

const EventGames = () => {
    const { id_event } = useParams();
    const [games, setGames] = useState([]);
    const [predictions, setPredictions] = useState({});
    const [userCods, setUserCods] = useState([]);
    const [loadingGames, setLoadingGames] = useState(true);
    const [loadingUserCods, setLoadingUserCods] = useState(true);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(false);
    const [submitButtonDisabled, setSubmitButtonDisabled] = useState(true);
    const { getEventGames } = useEventService();
    const { savePredictions } = useEventPredictionService();
    const { getUserCods } = useUserCodService();
    const { token } = useAuth();
    const [showSuccessModal, setShowSuccessModal] = useState(false);
    const [redirectToPredictions, setRedirectToPredictions] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                if (token && loadingGames) {
                    const data = await getEventGames(id_event);
                    setGames(data);
                    setLoadingGames(false);
                }
            } catch (error) {
                setError('No se pudieron obtener los partidos');
            }
        };

        fetchData();
    }, [token, loadingGames, getEventGames, id_event]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                if (token && loadingUserCods) {
                    const cods = await getUserCods();
                    setUserCods(cods);
                    setLoadingUserCods(false);
                }
            } catch (error) {
                setError('No se pudieron obtener los códigos del usuario');
            }
        };

        fetchData();
    }, [token, loadingUserCods, getUserCods]);

    const handlePredictionChange = (gameId, team, value) => {
        const numericValue = Number(value);
        if (!isNaN(numericValue) && numericValue >= 0) {
            setPredictions({
                ...predictions,
                [gameId]: {
                    ...predictions[gameId],
                    [team]: numericValue
                }
            });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (userCods.length === 0) {
            setError('No tienes códigos disponibles para guardar predicciones.');
            return;
        }

        const formattedPredictions = Object.entries(predictions).map(([gameId, { local_team, visit_team }]) => ({
            game: parseInt(gameId, 10),
            code: userCods[0].id,
            local_team_goals: local_team,
            visit_team_goals: visit_team,
        }));

        try {
            await savePredictions(id_event, formattedPredictions);
            setSuccess(true);

            Swal.fire({
                title: 'Predicciones Enviadas',
                text: 'Tus predicciones han sido enviadas exitosamente.',
                icon: 'success',
                confirmButtonText: 'Entendido',
                customClass: {
                  confirmButton: 'btn btn-success',
                },
                buttonsStyling: false,
            });


            // setShowSuccessModal(true);
        } catch (err) {
            console.error('Error saving predictions:', err);
            setError('No se pudieron guardar las predicciones');
        }
    };

    useEffect(() => {
        const allInputsFilled = games.every(game => 
            predictions[game.id]?.local_team !== undefined && predictions[game.id]?.visit_team !== undefined
        );
        setSubmitButtonDisabled(!allInputsFilled);
    }, [games, predictions]);

    useEffect(() => {
        if (success && !showSuccessModal) {
            setRedirectToPredictions(true);
        }
    }, [success, showSuccessModal]);

    if (loadingGames || loadingUserCods) {
        return (
            <div className="external-bg-container-include d-flex justify-content-center align-items-start pt-5 vh-100">
                <div className="container d-flex justify-content-center align-items-center flex-column">
                    <div className="col-12 m-0 p-0">
                        <h1 className="text-center title-text-ranking">Cargando...</h1>
                    </div>
                </div>
            </div>
        );
    }

    if (redirectToPredictions) {
        return <Navigate to="/predicciones" />;
    }

    return (

        <div className="events-container">
            <Header />
            <div className="external-bg-container-include d-flex justify-content-center align-items-start pt-3 vh-100">
                <div className="container d-flex justify-content-center align-items-center flex-column">
                    <div className="col-12 m-0 p-0">
                        <h3 className="text-center title-text-matches">Partidos del Evento</h3>
                        {error && <p className="error-message">{error}</p>}
                        {games.length === 0 ? (
                            <p>No hay partidos disponibles.</p>
                        ) : (
                            <form onSubmit={handleSubmit} className='d-flex justify-content-center align-items-center flex-column'>
                                <div className="playing-bet">
                                    <div className="container">
                                        <div className="accordion-collapse collapse show sports-expanded-list">
                                            <div className="accordion-body sports-body">
                                                <div className="tournament-title">
                                                    <span className="title-text">Partidos del Evento</span>
                                                </div>
                                                <div className="playing-sports-all table-matches-container">
                                                    {games.map(game => (
                                                        <div key={game.id} className="single-tournament">
                                                            <div className="tournament-title"></div>
                                                            <div className="all-tournament-match">
                                                                <div className="single-t-match">
                                                                    <div className="match-time">
                                                                        <span className="time-icon">
                                                                            <i className="fa-regular fa-clock"></i>
                                                                        </span>
                                                                        <span className="m-date">{game.date_game}</span>
                                                                        <span className="m-time">{game.time_game}</span>
                                                                    </div>
                                                                    {/* <div className="playing-teams">
                                                                        <div className="single-team mx-4">
                                                                            <div className="team-descr">
                                                                                <span className="team-icon">
                                                                                    <img src={game.local_team.flag_team} alt="" />
                                                                                </span>
                                                                                <span className="team-name">{game.local_team.name_team}</span>
                                                                            </div>
                                                                        </div>
                                                                        <div className="single-team mx-4">
                                                                            <div className="team-descr">
                                                                                <span className="team-icon">
                                                                                    <img src={game.visit_team.flag_team} alt="" />
                                                                                </span>
                                                                                <span className="team-name">{game.visit_team.name_team}</span>
                                                                            </div>
                                                                        </div>
                                                                    </div> */}
                                                                    <div className="placing-bet">
                                                                        <div className="single-bet-place">
                                                                            <span className="team-icon">
                                                                                <img src={game.local_team.flag_team} alt="" />
                                                                            </span>
                                                                            <span className="team-name">{game.local_team.name_team}</span>
                                                                            <input
                                                                                type="number"
                                                                                min="0"
                                                                                value={predictions[game.id]?.local_team ?? ''}
                                                                                onChange={(e) => handlePredictionChange(game.id, 'local_team', e.target.value)}
                                                                                placeholder="Marcador"
                                                                                className="container mt-2 mb-2 w-75 input-bet-send text-center"
                                                                            />
                                                                        </div>
                                                                        <div className="single-bet-place draw-box d-flex justify-content-center align-items-center">
                                                                            <span className="bet-ratio">VS</span>
                                                                        </div>
                                                                        <div className="single-bet-place">
                                                                            <span className="team-icon">
                                                                                <img src={game.visit_team.flag_team} alt="" />
                                                                            </span>
                                                                            <span className="team-name">{game.visit_team.name_team}</span>
                                                                            <input
                                                                                type="number"
                                                                                min="0"
                                                                                value={predictions[game.id]?.visit_team ?? ''}
                                                                                onChange={(e) => handlePredictionChange(game.id, 'visit_team', e.target.value)}
                                                                                placeholder="Marcador"
                                                                                className="container mt-2 mb-2 w-75 input-bet-send text-center"
                                                                            />
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    ))}
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <button type="submit" className={`btn btn-primary mt-3 ${submitButtonDisabled ? "disabled" : ""}`} disabled={submitButtonDisabled}>Enviar a Grupos</button>
                            </form>
                        )}
                    </div>
                </div>
            </div>
            <Link to="/predicciones" className="back-link">Volver a Eventos</Link>
            <Modal show={showSuccessModal} onHide={() => setShowSuccessModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Predicciones Enviadas</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Tus predicciones han sido enviadas exitosamente.
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowSuccessModal(false)}>Cerrar</Button>
                </Modal.Footer>
            </Modal>
        </div>              
    );
};

export default EventGames;
