import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import useEventService from '../services/eventService';
import { useAuth } from '../context/AuthContext';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState(null);
  const { getEvents } = useEventService();
  const { loading, token } = useAuth();

  const fetchEvents = useCallback(async () => {
    try {
      const data = await getEvents();
      setEvents(data);
    } catch (err) {
      setError(err.message || 'Failed to fetch events');
    }
  }, [getEvents]);

  useEffect(() => {
    if (!loading && token) {
      fetchEvents();
    }
  }, [loading, token, fetchEvents]);

  const handleEventClick = (event) => {
    // Implementa la lógica de redirección o muestra la información del evento aquí
    console.log(`Evento seleccionado: ${event.name}`);
  };

  if (loading) {
    return <div className="events-container"><p>Cargando...</p></div>;
  }

  return (
    <div className="events-container">
      <h2>Eventos</h2>
      {error && <p className="error-message">{error}</p>}
      {events.length === 0 ? (
        <p>No hay eventos disponibles.</p>
      ) : (
        <ul className="events-list">
          {events.map(event => (
            <li key={event.id} onClick={() => handleEventClick(event)}>
              {event.name}
            </li>
          ))}
        </ul>
      )}
      <Link to="/" className="back-button">Volver al Home</Link>
    </div>
  );
};

export default Events;
