import axios from 'axios';
import API_BASE_URL from '../config';

const eventPredictionsService = async (eventId, token) => {
  try {
    // Configura el encabezado de la solicitud con el token de autenticación
    const config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };

    // Realiza una solicitud GET al endpoint correspondiente, incluyendo el token en el encabezado
    const response = await axios.get(`${API_BASE_URL}/event-predictions/${eventId}/`, config);

    // Devuelve los datos de la respuesta
    return response.data;
  } catch (error) {
    // Maneja cualquier error de la solicitud
    throw new Error('Error al obtener las predicciones del evento');
  }
};

export default eventPredictionsService;
