import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const BASE_URL = 'https://api.locosporlapromo.com/api';

const checkCodService = async (cod, token) => {
  try {
    if (!token) {
      throw new Error('No token available');
    }

    const response = await axios.get(`${BASE_URL}/check-cod/?cod=${cod}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    return response.data;
  } catch (error) {
    throw new Error(error.response.data.message);
  }
};

const useCheckCodService = () => {
  const { token } = useAuth();
  return {
    checkCod: async (cod) => await checkCodService(cod, token),
  };
};

export default useCheckCodService;
