import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import useGameService from '../services/gameService'; // Importa el servicio de juegos
import './Games.css'; // Agrega el archivo CSS

const Games = () => {
  const { eventId } = useParams(); // Obtiene el ID del evento de los parámetros de la URL
  const { getGames } = useGameService(); // Obtén la función getGames del servicio

  const [games, setGames] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true); // Cambiado a true para iniciar la carga

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const data = await getGames(eventId); // Llama a la función getGames con el ID del evento
        setGames(data);
        setError(null); // Borra cualquier error previo en caso de éxito
      } catch (err) {
        setError(err.message || 'Failed to fetch games');
      } finally {
        setLoading(false); // Establece el estado de carga a false después de completar la solicitud
      }
    };

    if (loading) {
      fetchGames(); // Solo ejecuta fetchGames si el estado de carga es true
    }
  }, [eventId, getGames, loading]); // Asegúrate de incluir loading como dependencia

  return (
    <div className="games-container">
      <h2>Partidos del evento {eventId}</h2>
      {loading && <p>Cargando...</p>} {/* Muestra un mensaje de carga mientras se realiza la solicitud */}
      {error && <p className="error-message">{error}</p>}
      {games.length === 0 ? (
        <p>No hay partidos disponibles para este evento.</p>
      ) : (
        <ul className="games-list">
          {games.map(game => (
            <li key={game.id}>
              <p>ID: {game.id}</p>
              <p>Fecha del partido: {game.date_game}</p>
              <p>Hora del partido: {game.time_game}</p>
              <p>Equipo local: {game.local_team}</p>
              <p>Equipo visitante: {game.visit_team}</p>
              <p>Fecha de creación: {game.created}</p>
              <p>Fecha de modificación: {game.modified}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Games;
