import axios from 'axios';

const BASE_URL = 'https://api.locosporlapromo.com/api';

const checkCodService = async (cod) => {
  try {
    const response = await axios.get(`${BASE_URL}/check-cod/?cod=${cod}`);
    return response.data;
  } catch (error) {
    console.log(error)
    throw new Error(error.response.msg_response); // Imprime el error proporcionado por la API
  }
};

export default checkCodService;
